import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import TeacherCard from '../components/TeacherCard';
import SearchFilters from '../components/SearchFilters';

const HomePage = ({ user }) => {
  const [teachers, setTeachers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [topTeachers, setTopTeachers] = useState([]);

  useEffect(() => {
    fetchTeachers();
    fetchTopTeachers();
  }, []);

  const fetchTeachers = async (filters = {}) => {
    setLoading(true);
    try {
      const params = new URLSearchParams();
      Object.keys(filters).forEach(key => {
        if (filters[key]) {
          params.append(key, filters[key]);
        }
      });

      const response = await axios.get(`/teachers?${params}`);
      setTeachers(response.data || []);
    } catch (error) {
      console.error('Müəllimlər alınmadı:', error);
      setTeachers([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchTopTeachers = async () => {
    try {
      const response = await axios.get('/teachers/top/rated');
      setTopTeachers(response.data || []);
    } catch (error) {
      console.error('Top müəllimlər alınmadı:', error);
      setTopTeachers([]);
    }
  };

  const handleFilterChange = (filters) => {
    fetchTeachers(filters);
  };

  const handleSearch = (searchTerm) => {
    console.log('Axtarış:', searchTerm);
    fetchTeachers({ search: searchTerm });
  };

  const scrollToSearch = () => {
    const searchSection = document.getElementById('search-section');
    if (searchSection) {
      searchSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="gradient-bg text-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            Mükəmməl Müəllimi
            <br />
            <span className="text-yellow-300">Tap!</span>
          </h1>
          <p className="text-xl md:text-2xl mb-8 text-blue-100">
            Müəllim və abituriyentləri bir araya gətirən ağıllı platforma
          </p>
          
          {!user && (
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/register"
                className="bg-white text-blue-600 font-semibold px-8 py-4 rounded-lg hover:bg-gray-100 transition duration-200 transform hover:scale-105"
              >
                <i className="fas fa-user-plus mr-2"></i>
                Qeydiyyatdan Keç
              </Link>
              <Link
                to="/login"
                className="border-2 border-white text-white font-semibold px-8 py-4 rounded-lg hover:bg-white hover:text-blue-600 transition duration-200"
              >
                <i className="fas fa-sign-in-alt mr-2"></i>
                Giriş
              </Link>
            </div>
          )}
        </div>
      </section>

      {/* Statistics Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8 text-center">
            <div className="space-y-2">
              <div className="text-4xl font-bold text-blue-600">500+</div>
              <div className="text-gray-600">Müəllim</div>
            </div>
            <div className="space-y-2">
              <div className="text-4xl font-bold text-green-600">1000+</div>
              <div className="text-gray-600">Tələbə</div>
            </div>
            <div className="space-y-2">
              <div className="text-4xl font-bold text-purple-600">50+</div>
              <div className="text-gray-600">Fənn</div>
            </div>
            <div className="space-y-2">
              <div className="text-4xl font-bold text-orange-600">95%</div>
              <div className="text-gray-600">Məmnunluq</div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-800 mb-4">
              Niyə DərsTap?
            </h2>
            <p className="text-xl text-gray-600">
              Təhsil sahəsində rəqəmsal həllərimiz
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center p-6 bg-white rounded-xl shadow-lg">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-search text-2xl text-blue-600"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">Asan Axtarış</h3>
              <p className="text-gray-600">
                Şəhər, fənn, qiymət və reytinq üzrə filtrlərlə asan müəllim tapın
              </p>
            </div>

            <div className="text-center p-6 bg-white rounded-xl shadow-lg">
              <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-star text-2xl text-green-600"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">Şəffaf Reytinq</h3>
              <p className="text-gray-600">
                Həqiqi tələbə rəyləri və reytinqləri ilə doğru seçim edin
              </p>
            </div>

            <div className="text-center p-6 bg-white rounded-xl shadow-lg">
              <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-headset text-2xl text-purple-600"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">24/7 Dəstək</h3>
              <p className="text-gray-600">
                Həftənin 7 günü, günün 24 saatı dəstək xidməti
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Top Teachers Section */}
      {topTeachers.length > 0 && (
        <section className="py-16 bg-white">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center mb-12">
              <h2 className="text-3xl font-bold text-gray-800 mb-4">
                <i className="fas fa-trophy text-yellow-500 mr-3"></i>
                Ən Yaxşı Müəllimlər
              </h2>
              <p className="text-xl text-gray-600">
                Ən yüksək reytinqli və təcrübəli müəllimlərimiz
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {topTeachers.slice(0, 6).map((teacher) => (
                <TeacherCard key={teacher._id} teacher={teacher} />
              ))}
            </div>

            <div className="text-center mt-8">
              <button 
                onClick={scrollToSearch}
                className="btn-primary"
              >
                Bütün Müəllimləri Gör
              </button>
            </div>
          </div>
        </section>
      )}

      {/* Search Section */}
      <section id="search-section" className="py-16 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <SearchFilters 
            onFilterChange={handleFilterChange}
            onSearch={handleSearch}
          />

          {/* Results */}
          {loading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto"></div>
              <p className="text-gray-600 mt-4">Müəllimlər yüklənir...</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {teachers.length > 0 ? (
                teachers.map((teacher) => (
                  <TeacherCard key={teacher._id} teacher={teacher} />
                ))
              ) : (
                <div className="col-span-full text-center py-12">
                  <i className="fas fa-search text-6xl text-gray-300 mb-4"></i>
                  <h3 className="text-xl font-semibold text-gray-600 mb-2">
                    Müəllim tapılmadı
                  </h3>
                  <p className="text-gray-500">
                    Axtarış şərtlərini dəyişdirməyi yoxlayın
                  </p>
                </div>
              )}
            </div>
          )}
        </div>
      </section>

      {/* How it Works Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-800 mb-4">
              Necə İşləyir?
            </h2>
            <p className="text-xl text-gray-600">
              Sadə 3 addımda müəllim tapın
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 text-2xl font-bold">
                1
              </div>
              <h3 className="text-xl font-semibold mb-3">Axtarın</h3>
              <p className="text-gray-600">
                Şəhər, fənn və digər kriteriyalar üzrə axtarış edin
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-green-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 text-2xl font-bold">
                2
              </div>
              <h3 className="text-xl font-semibold mb-3">Seçin</h3>
              <p className="text-gray-600">
                Reytinq və rəylərə əsasən ən uyğun müəllimi seçin
              </p>
            </div>

            <div className="text-center">
              <div className="w-20 h-20 bg-purple-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 text-2xl font-bold">
                3
              </div>
              <h3 className="text-xl font-semibold mb-3">Əlaqə</h3>
              <p className="text-gray-600">
                Müəllimlə birbaşa əlaqə saxlayın və dərslərə başlayın
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-16 gradient-bg text-white">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            Təhsilin Gələcəyini Birlikdə İnşa Edək
          </h2>
          <p className="text-xl mb-8 text-blue-100">
            Biz təhsili daha əlçatan və şəffaf etmək üçün buradayıq. 
            Siz də bu yolda bizimləolun.
          </p>
          
          {!user ? (
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/register?role=teacher"
                className="bg-yellow-500 hover:bg-yellow-600 text-gray-900 font-semibold px-8 py-4 rounded-lg transition duration-200 transform hover:scale-105"
              >
                <i className="fas fa-chalkboard-teacher mr-2"></i>
                Müəllim kimi Qoşul
              </Link>
              <Link
                to="/register?role=student"
                className="bg-white text-blue-600 font-semibold px-8 py-4 rounded-lg hover:bg-gray-100 transition duration-200 transform hover:scale-105"
              >
                <i className="fas fa-graduation-cap mr-2"></i>
                Tələbə kimi Qoşul
              </Link>
            </div>
          ) : (
            <Link
              to="/dashboard"
              className="bg-white text-blue-600 font-semibold px-8 py-4 rounded-lg hover:bg-gray-100 transition duration-200 transform hover:scale-105 inline-block"
            >
              Dashboard-a Get
            </Link>
          )}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
