import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import axios from 'axios';

const RegisterPage = ({ onLogin }) => {
  const [searchParams] = useSearchParams();
  const [formData, setFormData] = useState({
    name: '',
    surname: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: searchParams.get('role') || 'student',
    phone: '',
    city: ''
  });

  const [teacherData, setTeacherData] = useState({
    subjects: [],
    experience: '',
    education: '',
    hourlyRate: '',
    grade: '',
    bio: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const cities = [
    'Bakı', 'Gəncə', 'Sumqayıt', 'Mingəçevir', 'Lənkəran', 
    'Şəki', 'Yevlax', 'Naxçıvan', 'Şirvan', 'Qəbələ'
  ];

  const subjects = [
    'Riyaziyyat', 'Fizika', 'Kimya', 'Biologiya', 'İngilis dili',
    'Rus dili', 'Azərbaycan dili', 'Tarix', 'Coğrafiya', 'İnformatika',
    'Ədəbiyyat', 'Həndəsə', 'Cəbr', 'Trigonometriya'
  ];

  const grades = [
    '1-4 sinif', '5-9 sinif', '10-11 sinif', 'Abituriyent', 
    'Universitet', 'Magistratura', 'Doktorantura'
  ];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleTeacherDataChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'subjects') {
      const selectedSubjects = Array.from(e.target.selectedOptions, option => option.value);
      setTeacherData({
        ...teacherData,
        subjects: selectedSubjects
      });
    } else {
      setTeacherData({
        ...teacherData,
        [name]: value
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Form validasiyası
    if (formData.password !== formData.confirmPassword) {
      setError('Şifrələr uyğun gəlmir');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Şifrə ən azı 6 simvol olmalıdır');
      setLoading(false);
      return;
    }

    // Müəllim məlumatları validasiyası
    if (formData.role === 'teacher') {
      if (teacherData.subjects.length === 0) {
        setError('Ən azı bir fənn seçin');
        setLoading(false);
        return;
      }
      if (!teacherData.experience || !teacherData.education || !teacherData.hourlyRate || !teacherData.grade) {
        setError('Bütün müəllim məlumatlarını doldurun');
        setLoading(false);
        return;
      }
    }

    try {
      const requestData = {
        ...formData,
        ...(formData.role === 'teacher' && { teacherData })
      };

      const response = await axios.post('/auth/register', requestData);
      const { token, user } = response.data;
      
      onLogin(user, token);
      alert('Qeydiyyat uğurludur! Xoş gəldiniz!');
    } catch (error) {
      setError(error.response?.data?.message || 'Qeydiyyat xətası baş verdi');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <div className="text-center mb-8">
          <div className="bg-blue-600 text-white w-16 h-16 rounded-xl flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-user-plus text-2xl"></i>
          </div>
          <h2 className="text-3xl font-bold text-gray-900">
            Hesab Yaradın
          </h2>
          <p className="mt-2 text-gray-600">
            DərsTap ailəsinə qoşulun
          </p>
        </div>

        <div className="bg-white rounded-xl shadow-lg p-8">
          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg mb-6">
              <i className="fas fa-exclamation-triangle mr-2"></i>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Role Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Rol Seçin
              </label>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                {[
                  { value: 'student', label: 'Tələbə', icon: 'graduation-cap' },
                  { value: 'teacher', label: 'Müəllim', icon: 'chalkboard-teacher' },
                  { value: 'parent', label: 'Valideyn', icon: 'users' }
                ].map((role) => (
                  <label
                    key={role.value}
                    className={`flex flex-col items-center p-4 border-2 rounded-lg cursor-pointer transition duration-200 ${
                      formData.role === role.value
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <input
                      type="radio"
                      name="role"
                      value={role.value}
                      checked={formData.role === role.value}
                      onChange={handleChange}
                      className="sr-only"
                    />
                    <i className={`fas fa-${role.icon} text-2xl mb-2 ${
                      formData.role === role.value ? 'text-blue-600' : 'text-gray-400'
                    }`}></i>
                    <span className={`font-medium ${
                      formData.role === role.value ? 'text-blue-600' : 'text-gray-700'
                    }`}>
                      {role.label}
                    </span>
                  </label>
                ))}
              </div>
            </div>

            {/* Personal Information */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                  Ad
                </label>
                <input
                  type="text"
                  name="name"
                  id="name"
                  required
                  value={formData.name}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Adınız"
                />
              </div>

              <div>
                <label htmlFor="surname" className="block text-sm font-medium text-gray-700 mb-2">
                  Soyad
                </label>
                <input
                  type="text"
                  name="surname"
                  id="surname"
                  required
                  value={formData.surname}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Soyadınız"
                />
              </div>
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                Email Ünvanı
              </label>
              <input
                type="email"
                name="email"
                id="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="input-field"
                placeholder="email@nümunə.com"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-2">
                  Şifrə
                </label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  required
                  value={formData.password}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Ən azı 6 simvol"
                />
              </div>

              <div>
                <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700 mb-2">
                  Şifrəni Təsdiq Et
                </label>
                <input
                  type="password"
                  name="confirmPassword"
                  id="confirmPassword"
                  required
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Şifrəni təkrarlayın"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Telefon
                </label>
                <input
                  type="tel"
                  name="phone"
                  id="phone"
                  required
                  value={formData.phone}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="+994XXXXXXXXX"
                />
              </div>

              <div>
                <label htmlFor="city" className="block text-sm font-medium text-gray-700 mb-2">
                  Şəhər
                </label>
                <select
                  name="city"
                  id="city"
                  required
                  value={formData.city}
                  onChange={handleChange}
                  className="input-field"
                >
                  <option value="">Şəhər seçin</option>
                  {cities.map((city) => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Teacher Specific Fields */}
            {formData.role === 'teacher' && (
              <div className="border-t border-gray-200 pt-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-4">
                  <i className="fas fa-chalkboard-teacher mr-2 text-blue-600"></i>
                  Müəllim Məlumatları
                </h3>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Fənnlər (bir neçə seçə bilərsiniz)
                    </label>
                    <select
                      name="subjects"
                      multiple
                      value={teacherData.subjects}
                      onChange={handleTeacherDataChange}
                      className="input-field h-32"
                      required
                    >
                      {subjects.map((subject) => (
                        <option key={subject} value={subject}>{subject}</option>
                      ))}
                    </select>
                    <p className="text-xs text-gray-500 mt-1">
                      Ctrl/Cmd + Click ilə bir neçə fənn seçin
                    </p>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Təcrübə (il)
                      </label>
                      <input
                        type="number"
                        name="experience"
                        value={teacherData.experience}
                        onChange={handleTeacherDataChange}
                        className="input-field"
                        placeholder="5"
                        min="0"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Saatlıq Ödəniş (₼)
                      </label>
                      <input
                        type="number"
                        name="hourlyRate"
                        value={teacherData.hourlyRate}
                        onChange={handleTeacherDataChange}
                        className="input-field"
                        placeholder="25"
                        min="1"
                        required
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Təhsil
                    </label>
                    <input
                      type="text"
                      name="education"
                      value={teacherData.education}
                      onChange={handleTeacherDataChange}
                      className="input-field"
                      placeholder="Bakı Dövlət Universiteti, Riyaziyyat fakültəsi"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Sinif/Səviyyə
                    </label>
                    <select
                      name="grade"
                      value={teacherData.grade}
                      onChange={handleTeacherDataChange}
                      className="input-field"
                      required
                    >
                      <option value="">Səviyyə seçin</option>
                      {grades.map((grade) => (
                        <option key={grade} value={grade}>{grade}</option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Haqqınızda (İstəyə bağlı)
                    </label>
                    <textarea
                      name="bio"
                      value={teacherData.bio}
                      onChange={handleTeacherDataChange}
                      rows="3"
                      className="input-field resize-none"
                      placeholder="Özünüz haqqında qısa məlumat..."
                      maxLength="500"
                    ></textarea>
                    <p className="text-xs text-gray-500 mt-1">
                      {teacherData.bio.length}/500 simvol
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Terms and Conditions */}
            <div className="flex items-center">
              <input
                id="terms"
                name="terms"
                type="checkbox"
                required
                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
              />
              <label htmlFor="terms" className="ml-2 block text-sm text-gray-700">
                <Link to="/terms" className="text-blue-600 hover:text-blue-500">
                  İstifadə şərtləri
                </Link>
                ni və{' '}
                <Link to="/privacy" className="text-blue-600 hover:text-blue-500">
                  məxfilik siyasətini
                </Link>
                ni qəbul edirəm
              </label>
            </div>

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full btn-primary text-lg py-3"
              >
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Qeydiyyat edilir...
                  </>
                ) : (
                  <>
                    <i className="fas fa-user-plus mr-2"></i>
                    Qeydiyyatdan Keç
                  </>
                )}
              </button>
            </div>

            {/* Login Link */}
            <div className="text-center">
              <p className="text-gray-600">
                Artıq hesabınız var?{' '}
                <Link to="/login" className="text-blue-600 hover:text-blue-500 font-medium">
                  Giriş edin
                </Link>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
